<div>
    <Popconfirm Title="Are you sure delete this task?"
                Visible="_visible"
                OnVisibleChange="OnVisibleChange"
                OnConfirm="Confirm"
                OnCancel="Cancel"
                OkText="Yes"
                CancelText="No">
