    void definitions(string propertyName, IFieldColumn column)
    {
        if (propertyName == "Tags")
        {
            var tagCol = ((Column<string[]>)column);

            tagCol.CellRender = cellData =>
            @<span>
                @foreach (var tag in cellData.FieldValue)
