                    if (tag == "loser")
                    {
                        color = TagColor.Volcano;
                    }
                    <Tag Color="@color">@tag</Tag>
                }
            </span>
    ;

            return;
        }

        column.Sortable = true;
        column.Filterable = true;
    }
}

