@using System.ComponentModel.DataAnnotations
@using System.Text.Json

<Alert Type="@AlertType.Info" Message="Fill in the form and refresh the page. The system will prompt you to recover the draft." ShowIcon="true" style="margin-bottom: 16px;" />

<DraftMonitor TData="FormModel"
              @ref="draftMonitor"
              Data="@model"
              DataChanged="@OnModelChanged"
              Options="@draftOptions"
              OnRecovered="@OnDraftRecovered"
              OnDeleted="@OnDraftDeleted" />

<Form Model="@model"
      OnFinish="OnFinish"
      OnFinishFailed="OnFinishFailed"
      LabelColSpan="8"
      WrapperColSpan="16">
    <FormItem Label="Username">
        <Input @bind-Value="@context.Username" />
    </FormItem>
    <FormItem Label="Email">
        <Input @bind-Value="@context.Email" />
    </FormItem>
    <FormItem Label="Password">
        <InputPassword @bind-Value="@context.Password" />
    </FormItem>
    <FormItem Label="Bio">
        <TextArea @bind-Value="@context.Bio" Rows="4" />
    </FormItem>
    <FormItem Label="Remember me" WrapperColOffset="8" WrapperColSpan="16">
        <Checkbox @bind-Value="context.RememberMe">Remember me</Checkbox>
    </FormItem>
    <FormItem WrapperColOffset="8" WrapperColSpan="16">
        <Space>
            <SpaceItem>
                <Button Type="@ButtonType.Primary" HtmlType="submit">
                    Submit
                </Button>
            </SpaceItem>
            <SpaceItem>
                <Button OnClick="ClearDraft">
                    Clear Draft
                </Button>
            </SpaceItem>
            <SpaceItem>
                <Button OnClick="Reset">
                    Reset
                </Button>
            </SpaceItem>
        </Space>
    </FormItem>
</Form>

@if (!string.IsNullOrEmpty(message))
{
    <Alert Type="@AlertType.Success" Message="@message" ShowIcon="true" style="margin-top: 16px;" Closable="true" OnClose="() => message = null" />
}

@code
{
    public class FormModel
    {
        [Required(ErrorMessage = "Please enter username")]
        public string Username { get; set; }

        [Required(ErrorMessage = "Please enter email")]
        [EmailAddress(ErrorMessage = "Please enter a valid email address")]
        public string Email { get; set; }

        [Required(ErrorMessage = "Please enter password")]
        [MinLength(6, ErrorMessage = "Password must be at least 6 characters")]
        public string Password { get; set; }

        public string Bio { get; set; }
        public bool RememberMe { get; set; }
    }

    private FormModel model = new FormModel();
    private string message;
    private DraftMonitor<FormModel> draftMonitor;

    private DraftOptions draftOptions = new DraftOptions
    {
        Key = "user-registration-form",
        DelayMilliseconds = 1000,
        RecoveryMode = DraftRecoveryMode.Confirm,
        Version = "1.0.0"
    };

    private void OnModelChanged(FormModel newModel)
    {
        model = newModel;
        StateHasChanged();
    }

    private void OnFinish(EditContext editContext)
    {
        message = $"Form submitted successfully! Data: {JsonSerializer.Serialize(model)}";
        Console.WriteLine(message);

        // Clear draft after successful submission
        _ = draftMonitor?.ClearDraftAsync();
    }

    private void OnFinishFailed(EditContext editContext)
    {
        message = "Form validation failed, please check your input!";
        Console.WriteLine(message);
    }

    private void OnDraftRecovered(FormModel recovered)
    {
        message = "Draft recovered!";
        Console.WriteLine($"Draft recovered: {JsonSerializer.Serialize(recovered)}");
    }

    private void OnDraftDeleted()
    {
        message = "Draft deleted!";
        Console.WriteLine("Draft deleted");
    }

    private async Task ClearDraft()
    {
        if (draftMonitor != null)
        {
            await draftMonitor.ClearDraftAsync();
            message = "Draft cleared!";
        }
    }

    private void Reset()
    {
        model = new FormModel();
        message = "Form reset!";
    }
}

@using System.Text.Json

<DraftMonitor TData="FormModel" 
              Data="@_model" 
              DataChanged="@((v) => _model = v)"
              Options="@(new DraftOptions { Key = "demo-custom-template" })"
              RecoveryContentTemplate="@RecoveryTemplate" />

<Form Model="@_model">
    <FormItem Label="Username">
        <Input @bind-Value="@context.Username" />
    </FormItem>
    <FormItem Label="Email">
        <Input @bind-Value="@context.Email" />
    </FormItem>
    <FormItem Label="Remarks">
        <TextArea @bind-Value="@context.Remarks" Rows="4" />
    </FormItem>
</Form>

@code {
    private FormModel _model = new FormModel();

    private RenderFragment<DraftData<FormModel>> RecoveryTemplate => draft => @<div>
        <Alert Type="@AlertType.Info" Message="Unfinished draft detected" Description="@GetDraftSummary(draft)" ShowIcon="true" />
        <Divider />
        <Descriptions Title="Draft Details" Bordered Size="@DescriptionsSize.Small">
            <DescriptionsItem Title="Saved Time">@draft.SavedAt.ToLocalTime().ToString("yyyy-MM-dd HH:mm:ss")</DescriptionsItem>
            <DescriptionsItem Title="Version">@draft.Version</DescriptionsItem>
            <DescriptionsItem Title="Username">@draft.Data.Username</DescriptionsItem>
            <DescriptionsItem Title="Email">@draft.Data.Email</DescriptionsItem>
            <DescriptionsItem Title="Remarks" Span="3">@draft.Data.Remarks</DescriptionsItem>
        </Descriptions>
        <br />
        <p><Text Type="@TextElementType.Secondary">Click "OK" to restore the draft, or "Cancel" to delete it</Text></p>
    </div>;

    private string GetDraftSummary(DraftData<FormModel> draft)
    {
        var time = DateTime.Now - draft.SavedAt.ToLocalTime();
        var timeDesc = time.TotalDays >= 1 ? $"{(int)time.TotalDays} days ago" :
                       time.TotalHours >= 1 ? $"{(int)time.TotalHours} hours ago" :
                       time.TotalMinutes >= 1 ? $"{(int)time.TotalMinutes} minutes ago" :
                       "just now";
        return $"Found draft saved {timeDesc}, containing {GetFieldCount(draft.Data)} filled field(s).";
    }

    private int GetFieldCount(FormModel model)
    {
        int count = 0;
        if (!string.IsNullOrEmpty(model.Username)) count++;
