@layout AntDesign.Docs.Demos.Experimental.ReuseTabs.demo.Layout_
@inject NavigationManager navigationManager
@implements IDisposable
@implements IReuseTabsPage

@page "/reuse/Singleton"
@page "/reuse/Singleton/{Id:int}"

@attribute [ReuseTabsPage(Singleton = true)]

<PageTitle>Singleton Page</PageTitle>

<h1>Singleton Page</h1>

<p>@text</p>

<p>@dynamicText</p>

<p><a href="/reuse/Singleton/@(NextId)?query=@(Random.Shared.Next())">Navigate to next id: @NextId</a></p>


@code{

    [Parameter] public int Id { get; set; }

    [SupplyParameterFromQuery] public string Query { get; set; }

    private int NextId => Id + 1;

    string text = "";
    string dynamicText = "";

    protected override void OnInitialized()
    {
        text = "OnInitialized only be called once, Id = " + Id;
        navigationManager.LocationChanged += OnLationChanged;
    }

    private void OnLationChanged(object sender, EventArgs e)
    {
        dynamicText = $"Current Id = {Id}, Query = {Query}";
        InvokeAsync(StateHasChanged);
    }

    public RenderFragment GetPageTitle()
    {
        return @<span> Singleton Page with Id @Id </span>;
    }

    public void Dispose()
    {
        navigationManager.LocationChanged -= OnLationChanged;
    }
}

@layout AntDesign.Docs.Demos.Experimental.ReuseTabs.demo.Layout_
@page "/reuse/non-route-data"

<Button OnClick="Update">Update settings</Button>

@inject ReuseTabsService ReuseTabsService
@code {

    [CascadingParameter]
    private Routes Routes { get; set; }

    protected override void OnInitialized()
    {
        Routes.SetCascadingRouteData(false);
    }

    private void Update()
    {
        ReuseTabsService.UpdatePage(options =>
        {
            options.Title = b => b.AddContent(1, $"New Title {Random.Shared.Next(0, 1000)}");
            options.Order = Random.Shared.Next(0, 1000);
            options.Closable = !options.Closable;
        });
    }
}

@layout AntDesign.Docs.Demos.Experimental.ReuseTabs.demo.Layout_

@page "/reuse"
@attribute [ReuseTabsPage(Pin = true, Closable = false)]

<PageTitle>Home</PageTitle>

<h1>Home</h1>

@using Microsoft.AspNetCore.Components.Rendering

<Flex Gap="FlexGap.Middle" Vertical>
    @CustomSplitter("height: 200px;")
    @CustomSplitter("height: 300px;", SplitterLayout.Vertical)
</Flex>

@code {
    void RenderDesc(RenderTreeBuilder __builder, string text)
    {
        <Flex Justify="FlexJustify.Center" align="FlexAlign.Center" Style="height: 100%">
            <Title Type="TextElementType.Secondary" Level="5" Style="white-space: nowrap">
                @text
            </Title>
        </Flex>
    }

    RenderFragment Desc(string text) => b => RenderDesc(b, text);

    void RenderCustomSplitter(RenderTreeBuilder __builder, string style, SplitterLayout layout)
    {
        <Splitter Style="@($"box-shadow: 0 0 10px rgba(0, 0, 0, 0.1); {style}")" Layout="layout">
            <SplitterPanel Collapsible Min="20%">
                @Desc("First")
            </SplitterPanel>
            <SplitterPanel Collapsible>
                @Desc("Second")
            </SplitterPanel>
        </Splitter>
    }

    RenderFragment CustomSplitter(string style, SplitterLayout layout = SplitterLayout.Horizontal)
    => b => RenderCustomSplitter(b, style, layout);
}

